
-- init.sql
-- This script runs on database startup

-- Check if the database exists, and create it if it does not
IF NOT EXISTS (SELECT * FROM sys.databases WHERE name = 'PixelPainter')
BEGIN
    CREATE DATABASE PixelPainter;
END
GO

USE PixelPainter;
GO
IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'Artist')
BEGIN
    CREATE TABLE Artist (
        Id INT IDENTITY(1,1) NOT NULL,
        SubId VARCHAR(21) NOT NULL,
        [Name] VARCHAR(20) NOT NULL,
        Email NVARCHAR(40),
        IsAdmin BIT DEFAULT 0, -- this is a bool, 0 = 0 1 = 1
        CreationDate DATETIME DEFAULT GETDATE(),
        PrivateProfile BIT DEFAULT 0,
        PRIMARY KEY (Id)
    );
END
GO
IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'Art')
BEGIN
    CREATE TABLE Art (
        Id INT IDENTITY(1,1) NOT NULL,
        Title NVARCHAR(255),
        IsPublic BIT DEFAULT 0,
        CreationDate DATETIME DEFAULT GETDATE(),
        IsGIF BIT DEFAULT 0,
        gifId INT DEFAULT 0,
        gifFrameNum INT DEFAULT 0,
        Width INT,
        Height INT,
        Encode VARCHAR(MAX),
        PRIMARY KEY (Id),
    );
END
GO

IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'Likes')
BEGIN
    CREATE TABLE Likes (
        ArtistId INT,
        ArtId INT,
        CreationDate DATETIME DEFAULT GETDATE(),
        Viewed INT DEFAULT 0,
        PRIMARY KEY (ArtistId, ArtId),
        CONSTRAINT FK_Likes_Art FOREIGN KEY (ArtId) REFERENCES Art(Id) ON DELETE CASCADE
    );
END
GO
IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'Dislikes')
BEGIN
    CREATE TABLE Dislikes (
        ArtistId INT,
        ArtId INT,
        CreationDate DATETIME DEFAULT GETDATE(),
        Viewed INT DEFAULT 0,
        PRIMARY KEY (ArtistId, ArtId),
        CONSTRAINT FK_Dislikes_Art FOREIGN KEY (ArtId) REFERENCES Art(Id) ON DELETE CASCADE
    );
END
GO
IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'ContributingArtists')
BEGIN
    CREATE TABLE ContributingArtists (
        ArtistId INT,
        ArtId INT,
        CreationDate DATETIME DEFAULT GETDATE(),
        PRIMARY KEY (ArtistId, ArtId),
    );
END
GO
IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'Comment')
BEGIN
    CREATE TABLE Comment (
        Id INT IDENTITY(1,1) NOT NULL,
        ArtistId INT,
        ArtId INT,
        ReplyId INT,
        [Message] NVARCHAR(2222),
        CreationDate DATETIME DEFAULT GETDATE(),
        Viewed INT DEFAULT 0,
        PRIMARY KEY (Id),
        CONSTRAINT FK_Comment_Art FOREIGN KEY (ArtId) REFERENCES Art(Id) ON DELETE CASCADE,
    );
END
GO
IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'CommentDislikes')
BEGIN
    CREATE TABLE CommentDislikes (
        ArtistId INT,
        CommentId INT,
        CreationDate DATETIME DEFAULT GETDATE(),
        Viewed INT DEFAULT 0,
        PRIMARY KEY (ArtistId, CommentId),
        CONSTRAINT FK_CommentDislikes_Art FOREIGN KEY (CommentId) REFERENCES Comment(Id) ON DELETE CASCADE
    );
END
GO
IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'CommentLikes')
BEGIN
    CREATE TABLE CommentLikes (
        ArtistId INT,
        CommentId INT,
        CreationDate DATETIME DEFAULT GETDATE(),
        Viewed INT DEFAULT 0,
        PRIMARY KEY (ArtistId, CommentId),
        CONSTRAINT FK_CommentLikes_Art FOREIGN KEY (CommentId) REFERENCES Comment(Id) ON DELETE CASCADE
    );
END
GO

IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'GIF')
BEGIN
    CREATE TABLE GIF (
        Id INT IDENTITY(1,1) NOT NULL,
        FPS INT,
        PRIMARY KEY (Id)
    );
END
GO

-- Tag table
IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'Tag')
BEGIN
    CREATE TABLE Tag (
        Id INT IDENTITY(1,1) NOT NULL,
        [Name] NVARCHAR(100) NOT NULL,
        CreationDate DATETIME DEFAULT GETDATE(),
        PRIMARY KEY (Id)
    );
END
GO

-- ArtTags table
IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'ArtTags')
BEGIN
    CREATE TABLE ArtTags (
        ArtId INT NOT NULL,
        TagId INT NOT NULL,
        CreationDate DATETIME DEFAULT GETDATE(),
        PRIMARY KEY (ArtId, TagId),
        CONSTRAINT FK_ArtTags_Art FOREIGN KEY (ArtId) REFERENCES Art(Id) ON DELETE CASCADE,
        CONSTRAINT FK_ArtTags_Tag FOREIGN KEY (TagId) REFERENCES Tag(Id) ON DELETE CASCADE
    );
END
GO

