CREATE TRIGGER CommentDeleteTrigger ON dbo.Comment
	for delete
AS 
	delete from Comment
	where ReplyId in(select deleted.id from deleted)
	GO
CREATE TRIGGER UserDeleteTrigger ON dbo.Artist
	for delete
AS 
	delete from Comment
	where Comment.ArtistId in(select deleted.id from deleted)
    delete from ContributingArtists
    where ContributingArtists.ArtistId in(select deleted.id from deleted)
	delete from Likes
	where Likes.ArtistId in(select deleted.id from deleted)
	delete from likes
	where likes.ArtistId in(select deleted.id from deleted)
	GO

Create trigger ContributingArtistDeleteTrigger ON ContributingArtists
	for delete
AS
delete from Art where id not in (select artid from ContributingArtists where ArtId is not null) and art.gifFrameNum <= 1;
GO

CREATE TRIGGER GifDeleteTrigger
ON Art
AFTER DELETE
AS
BEGIN
    DELETE A
    FROM Art A
    INNER JOIN deleted D ON A.gifId = D.gifId
    WHERE A.Id != D.Id AND D.gifId != 0;
END;
GO

